function parse($inputFile, $sane)
{
    if ($sane) {
        parse2($inputFile, $sane);
    } else {
        parse1($inputFile, $sane);
    }
}

function parse1($inputFile, $sane)
{
    $handle = fopen($inputFile, "r");

    $words = [];
    if ($handle) {
        while (($line = fgets($handle)) !== false) {
            $line = trim($line);
            echo $line . "\n";
            if (
                strpos($line, "-") !== false ||
                strpos($line, " ") !== false ||
                strpos($line, "'") !== false ||
                strpos($line, ".") !== false ||
                substr($line, 0, 1) !== strtolower(substr($line, 0, 1))
            ) {
                continue;
            }
            $line = transliterateString($line);

            $lineCompare = str_replace("ij", "|", $line);
            $lineCompare = str_replace("IJ", "|", $lineCompare);
            if (strlen($lineCompare) >= 5 && strlen($lineCompare) <= 10 && strlen($lineCompare) != 9) {
                $words[] = strtoupper($line) . ",nl," . strlen($lineCompare) . "," . ($sane ? "1" : "0");
            }
        }

        fclose($handle);
    } else {
        // error opening the file.
    }

    file_put_contents($inputFile . ".csv", implode("\r\n", $words));
}

